import os

def delete_dwg_files(directory, files_to_delete):
    """
    Delete specified DWG files from the given directory.
    
    :param directory: The directory path containing the files
    :param files_to_delete: A list of filenames (without extension) to delete
    :return: A list of files that were successfully deleted
    """
    deleted_files = []
    
    for filename in files_to_delete:
        file_path = os.path.join(directory, f"{filename}.dwg")
        
        if os.path.exists(file_path):
            try:
                os.remove(file_path)
                deleted_files.append(filename)
                print(f"Deleted: {filename}.dwg")
            except Exception as e:
                print(f"Error deleting {filename}.dwg: {str(e)}")
        else:
            print(f"File not found: {filename}.dwg")
    
    return deleted_files

